<?php/*** Lets a user attack, and handles the reaction to that.** Returns a json script, handled by the javascript on fight.php.*/define ('LOGIN', true);require_once ('../includes/notextinc.php');// the reponse will be stored in here, and then json'd and echoed later$r = array ();// the character needs to be in a fight to be hereif ($Character->getMapData()->getDetail ('phase') != "fight") {	$r['status'] = "bad phase";} else {	$r['status'] = "success";	$r['attack'] = $Character->getFightData()->attack ();}echo json_encode ($r);?>