<?php/*** AJAX JSON returning script. Returns data about what actions are currently possible.*/define ('LOGIN', true);require_once ('../includes/notextinc.php');$return = array ();// the character needs to be in a fight to be hereif ($Character->getMapData()->getDetail ('phase') != "fight") {	$return['status'] = "failure";	$return['message'] = "Player needs to be in a fight to use this page.";} else {	$return['status'] = "success";	$return['items'] = array ();	// go through each of the items the user has...	$Items = $Character->getInventory()->getItems();	foreach ($Items as $Il) {		// any actions for this item?		$actions = $Il['Item']->getActions ($Character);		if (!empty ($actions)) {			$return['items'][] = array (				'id' => $Il['Item']->getId(),				'name' => $Il['Item']->getName(),				'qty' => $Il['qty'],				'actions' => $actions			);		}	}}echo json_encode ($return);?>