<?php/*** Lets the mob do whatever action they decide to take.* * Needs to be included by one of the user's action scripts. Manipulates the $r response array.*/if (!is_array ($r)) exit;// it nearly always fights. actually, always.$r['mob']['attack'] = $Mob->attack ($Character);// did their attack connect?if ($r['mob']['attack']['hit']) {	$Message = FightMessage::addMessage ($turn_id, 2, array ($r['mob']['attack']['hit_amount']));	$r['message'][] = $Message->getMessageArray ();		// did the mob kill us?	if ($Character->getDetail ('remaining_hp') <= 0) {		$Message = FightMessage::addMessage ($turn_id, 9);		$r['message'][] = $Message->getMessageArray ();	}} else {	// they missed	$Message = FightMessage::addMessage ($turn_id, 4);	$r['message'][] = $Message->getMessageArray ();}?>