<?php/*** Lets the mob do whatever action they decide to take.* * Needs to be included by one of the user's action scripts. Manipulates the $r response array.*/if (!is_array ($r)) exit;// it nearly always fights. actually, always.$r['mob']['attack'] = $Mob->attack ($Character);if ($r['mob']['attack']['hit']) {	$r['message'][] = array ('msg' => "A mob blasts you for <strong>".$r['mob']['attack']['hit_amount']."</strong> damage.", 'type' => "red");		// did the mob kill us?	if ($Character->getDetail ('remaining_hp') <= 0) {		$r['message'][] = array ('msg' => "A mob defeated you!", 'type' => "red");		$r['fight_stage'] = $Fight->getStage();	}} else {	$r['message'][] = array ('msg' => "A mob attacks you, but misses!", 'type' => "red");}?>