<?phpdefine ('LOGIN', 1);// I'd like to force this to "only if the user is looking at the invent page" but "invent" isn't a stage...// come back to this later.require_once ('../includes/notextinc.php');// we need an item_idif (!is_numeric ($_GET['item_id'])) echo "<p>Something weird happened with the page load.</p>\n";$page_content = array ('name' => '', 'description' => '');$Item = new Item ($_GET['item_id']);$page_content['item_id'] = $Item->getId();$page_content['name'] = ucfirst ($Item->getName ());$page_content['description'] = "<p>You can carry ".$Item->getMaxQty()." of these.</p>\n";$page_content['actions'] = $Item->getActions ();echo json_encode ($page_content);?>