<?php/*** Script, usually called by AJAX, which returns a JSON string, giving feedback on an* attempt to unequip an item.** Requires a $_GET['position'].*/define ('LOGIN', 1);require_once ('../includes/notextinc.php');header('Content-type: application/json');$return = array ();$return['status'] = "success";if (empty ($_GET['position'])) {	$return['status'] = "failure";	$return['status_message'] = "Missing position parameter.";} else {	$return['position'] = $_GET['position'];	$Item = $Character->getEquippedItem ($_GET['position']);	if ($Item) {		$return['item_found'] = true;				// attempt to unequip, which may lead to an exception since unequipping puts the item back into the invent, however		// if the Invent can't handle more, it'll throw an exception.		try {			$Character->unequipItem ($_GET['position']);			$return['unequip_success'] = true;		} catch (Exception $e) {			// we're only expecting error code 100			if ($e->getCode() == 100) {				// we can't unequip because we can't put the item anywhere..				$return['unequip_success'] = false;			} else {				throw $e;			}		}	} else {		$return['item_found'] = false;	}}echo json_encode ($return);?>